INSERT INTO `db_list_courses` (`course_id`, `course_photo`, `course_name`, `course_price`, `course_author_name`, `course_about`, `course_introduction`, `course_for_who_is`, `course_what_will_you_learn`, `course_author_info`, `course_date_started`, `status`) VALUES
(1, '1.jpg', 'Бизнеске 7 қадам', 9990, 'Еңлік Әбдіқадыр', '<p>Бұл курс:</p>  <ul> 	<li>Кәсіпкерлік мінез қалыптастыру;</li> 	<li>Қабілетті анықтап, оны қаражатқа айналдыру;</li> 	<li>Өз салаңды қатесіз тауып, бизнес ережелерімен танысу;</li> 	<li>Маркетинг техникалары;</li> 	<li>Команда құру және оны басқару жолдары;</li> 	<li>Кәсіптегі 4 формула;</li> 	<li>Нәтижеге жетудің негізгі құпиялары айтылатын видео-сабақтардан тұрады.&nbsp;</li> </ul>', 'https://www.youtube.com/embed/Mze_Ht3Dx-c', '<p>Бизнеске 7 қадам курсы - кәсіп ашып, өз саласын тауып, тұрақты табысты көздеген, кәсіптік білімімді арттырсам деген кез келген адамға арналады. Жалпы аудиториясына шектеу жоқ.</p>', '<p>Кәсіпке қатысты нақты 7 қадаммен танысып, бизнестің қиындығы мен қызығы, кәсіпте білу керек өте маңызды,&nbsp;4 басты формуланы меңгересің. Жеке кәсібіңді оңай, капиталсыз бастап кетудің жолдарын үйренесің. Команда басқару, маркетинг, дұрыс жарнаманың әдістері мен нәтижеге тез жету тәсілдерін түсінесің.</p>', '<p>Бұл курсты журналист, практик-психолог, НЛП-мастер, бизнес тренер, тұлғалық даму және кәсіпті дамыту бойынша коуч-маман,&nbsp;<br /> 3 кәсіптің негізін қалаушы, &quot;БРИЛЛИАНТ ЛЕДИ&quot; кітабының авторы, &quot;Армандар орындалады&quot; республикалық журналының директоры - Еңлік Әбдіқадыр жүргізеді.<br /> 2014 жылдан бастап жеке кәсіппен айналысып, өз тәжірибесімен бөлісіп жүрген жеке кәсіпкер.<br /> Бүгінде &nbsp;1000-ға жуық адамдарға өз семинар тренингін өткізіп, 100-ге жуық қатысушыларға коучинг-сессия жасап, көптеген жеке кәсіпкерді дайындап шығарған маман.</p>', '01.09.2020', 1),
(3, '', 'Тест', 5111, 'Тест автор', '<p>asd</p>\r\n', '<p>asd</p>\r\n', '<p>asd</p>\r\n', '<p>asd</p>\r\n', '<p>asd</p>\r\n', '25.11.2020', 0),
(4, '', 'Тест курс', 0, '12345', '', '', '', '', '', '', 0);

